import formatMessage from "../format-message";
import * as strings from "./strings";
const WORD_COUNT = 4;
export default function describe(elem) {
  if (!elem || !elem.tagName) {
    return null;
  }

  switch (elem.tagName) {
    case "IMG":
      return "\u0E20\u0E32\u0E1E\u0E1E\u0E23\u0E49\u0E2D\u0E21\u0E0A\u0E37\u0E48\u0E2D\u0E44\u0E1F\u0E25\u0E4C " + strings.filename(elem.src);

    case "TABLE":
      return "\u0E15\u0E32\u0E23\u0E32\u0E07\u0E40\u0E23\u0E34\u0E48\u0E21\u0E15\u0E49\u0E19\u0E14\u0E49\u0E27\u0E22 " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "A":
      return "\u0E25\u0E34\u0E07\u0E04\u0E4C\u0E17\u0E35\u0E48\u0E21\u0E35\u0E02\u0E49\u0E2D\u0E04\u0E27\u0E32\u0E21\u0E40\u0E23\u0E34\u0E48\u0E21\u0E15\u0E49\u0E19\u0E14\u0E49\u0E27\u0E22 " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "P":
      return "\u0E22\u0E48\u0E2D\u0E2B\u0E19\u0E49\u0E32\u0E17\u0E35\u0E48\u0E40\u0E23\u0E34\u0E48\u0E21\u0E15\u0E49\u0E19\u0E14\u0E49\u0E27\u0E22 " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "TH":
      return "\u0E2B\u0E31\u0E27\u0E40\u0E23\u0E37\u0E48\u0E2D\u0E07\u0E15\u0E32\u0E23\u0E32\u0E07\u0E40\u0E23\u0E34\u0E48\u0E21\u0E15\u0E49\u0E19\u0E14\u0E49\u0E27\u0E22 " + strings.firstWords(elem.textContent, WORD_COUNT);

    case "H1":
    case "H2":
    case "H3":
    case "H4":
    case "H5":
      return "\u0E2B\u0E31\u0E27\u0E40\u0E23\u0E37\u0E48\u0E2D\u0E07\u0E40\u0E23\u0E34\u0E48\u0E21\u0E15\u0E49\u0E19\u0E14\u0E49\u0E27\u0E22 " + strings.firstWords(elem.textContent, WORD_COUNT);

    default:
      return "\u0E2D\u0E07\u0E04\u0E4C\u0E1B\u0E23\u0E30\u0E01\u0E2D\u0E1A\u0E40\u0E23\u0E34\u0E48\u0E21\u0E15\u0E49\u0E19\u0E14\u0E49\u0E27\u0E22 " + strings.firstWords(elem.textContent, WORD_COUNT);
  }
}